package lecture_07;

public class char_type {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c= 'a';
		int a = (int)(c);
		System.out.println(c);
		System.out.println(a);
		c++;
		System.out.println(c);
	}
	
	// When we want to keep an integer in character we have to type cast its value
	// The internal type casting is called implicity type casting
	 

}
